mapper.delete:
delete from oauth_app_detail
where id in

    #{id}

mapper.select:
select 
from oauth_app_detail


        and id like #{id}


        and app_id like #{appId}


        and app_name like #{appName}


        and app_secret like #{appSecret}


        and redirect_uri like #{redirectUri}


        and description like #{description}


        and status like #{status}


        and create_user like #{createUser}


        and update_user like #{updateUser}


        and create_time like #{createTime}


        and update_time like #{updateTime}
